def silu(x):
    # SiLU(x) = x * sigmoid(x)
    return x * torch.sigmoid(x)

def gelu(x):
    # GELU(x) = 0.5 * x * (1 + tanh( sqrt(2/pi)*(x + 0.044715*x^3) ))
    c = torch.sqrt(torch.tensor(2.0 / torch.pi))
    return 0.5 * x * (1.0 + torch.tanh(c * (x + 0.044715 * x ** 3)))

def relu(x):
    # ReLU(x) = max(0, x)
    return torch.maximum(x, torch.zeros_like(x))

ACT2FN = {
    "silu": silu,
    "gelu": gelu,
    "relu": relu,
}

class Qwen2MLP(nn.Module):
    def __init__(self, config):
        super().__init__()
        self.hidden_size = config.hidden_size
        self.intermediate_size = config.intermediate_size
        self.gate_proj = nn.Linear(self.hidden_size, self.intermediate_size, bias=False)
        self.up_proj   = nn.Linear(self.hidden_size, self.intermediate_size, bias=False)
        self.down_proj = nn.Linear(self.intermediate_size, self.hidden_size, bias=False)
        self.act_fn = ACT2FN[config.hidden_act]

    def forward(self, x):
        gate_out = self.gate_proj(x)
        gate_act = self.act_fn(gate_out)
        up_out = self.up_proj(x)
        gated_up = gate_act * up_out
        down_out = self.down_proj(gated_up)
        return down_out


def rotate_half(x):
    """Rotates half the hidden dims of the input."""
    x1 = x[..., : x.shape[-1] // 2]
    x2 = x[..., x.shape[-1] // 2 :]
    return torch.cat((-x2, x1), dim=-1)


def apply_rotary_pos_emb(q, k, cos, sin, position_ids=None, unsqueeze_dim=1):
    """Applies Rotary Position Embedding to the query and key tensors.

    Args:
        q (`torch.Tensor`): The query tensor.
        k (`torch.Tensor`): The key tensor.
        cos (`torch.Tensor`): The cosine part of the rotary embedding.
        sin (`torch.Tensor`): The sine part of the rotary embedding.
        position_ids (`torch.Tensor`, *optional*):
            Deprecated and unused.
        unsqueeze_dim (`int`, *optional*, defaults to 1):
            The 'unsqueeze_dim' argument specifies the dimension along which to unsqueeze cos[position_ids] and
            sin[position_ids] so that they can be properly broadcasted to the dimensions of q and k. For example, note
            that cos[position_ids] and sin[position_ids] have the shape [batch_size, seq_len, head_dim]. Then, if q and
            k have the shape [batch_size, heads, seq_len, head_dim], then setting unsqueeze_dim=1 makes
            cos[position_ids] and sin[position_ids] broadcastable to the shapes of q and k. Similarly, if q and k have
            the shape [batch_size, seq_len, heads, head_dim], then set unsqueeze_dim=2.
    Returns:
        `tuple(torch.Tensor)` comprising of the query and key tensors rotated using the Rotary Position Embedding.
    """
    cos = cos.unsqueeze(unsqueeze_dim)
    sin = sin.unsqueeze(unsqueeze_dim)
    q_embed = (q * cos) + (rotate_half(q) * sin)
    k_embed = (k * cos) + (rotate_half(k) * sin)
    return q_embed, k_embed

def zeros(shape):
    """
    Táº¡o tensor chá»©a toÃ n sá»‘ 0
    shape: tuple kÃ­ch thÆ°á»›c (1D, 2D, 3D hoáº·c 4D)
    """
    if len(shape) == 0:  # scalar
        return 0
    if len(shape) == 1:  # vector
        return [0 for _ in range(shape[0])]
    elif len(shape) == 2:  # ma tráº­n
        return [[0 for _ in range(shape[1])] for _ in range(shape[0])]
    elif len(shape) == 3:  # tensor 3D
        return [[[0 for _ in range(shape[2])] for _ in range(shape[1])] for _ in range(shape[0])]
    elif len(shape) == 4:  # tensor 4D
        return [[[[0 for _ in range(shape[3])] 
                 for _ in range(shape[2])] 
                for _ in range(shape[1])] 
               for _ in range(shape[0])]
    else:
        raise ValueError(f"KhÃ´ng há»— trá»£ tensor vá»›i {len(shape)} chiá»u")

def tensor(data):
    """
    MÃ´ phá»ng torch.tensor
    data cÃ³ thá»ƒ lÃ  sá»‘ hoáº·c list nhiá»u chiá»u
    """
    if isinstance(data, (int, float)):   # Scalar
        return {"data": data, "shape": ()}
    
    if isinstance(data, list):
        # HÃ m phá»¥ Ä‘á»ƒ tÃ­nh shape
        def get_shape(x):
            if isinstance(x, list):
                return (len(x),) + get_shape(x[0]) if x else (0,)
            else:
                return ()
        shape = get_shape(data)
        return {"data": data, "shape": shape}

def matmul(A, B, transpose_B=False):
    """
    NhÃ¢n ma tráº­n cho tensor nhiá»u chiá»u
    Há»— trá»£:
    - Ma tráº­n 2D: A[m,n] @ B[n,p] -> C[m,p]
    - Tensor 4D: A[batch,heads,seq_q,dim] @ B[batch,heads,seq_k,dim] -> C[batch,heads,seq_q,seq_k]
    """
    # XÃ¡c Ä‘á»‹nh sá»‘ chiá»u cá»§a A vÃ  B
    if not isinstance(A[0], list):  # A lÃ  vector 1D
        dim_A = 1
    elif not isinstance(A[0][0], list):  # A lÃ  ma tráº­n 2D
        dim_A = 2
    elif not isinstance(A[0][0][0], list):  # A lÃ  tensor 3D
        dim_A = 3
    else:  # A lÃ  tensor 4D
        dim_A = 4
        
    if not isinstance(B[0], list):  # B lÃ  vector 1D
        dim_B = 1
    elif not isinstance(B[0][0], list):  # B lÃ  ma tráº­n 2D
        dim_B = 2
    elif not isinstance(B[0][0][0], list):  # B lÃ  tensor 3D
        dim_B = 3
    else:  # B lÃ  tensor 4D
        dim_B = 4
    
    # TrÆ°á»ng há»£p ma tráº­n 2D
    if dim_A == 2 and dim_B == 2:
        m, n = len(A), len(A[0])
        n2 = len(B)
        p = len(B[0])
        assert n == n2, f"KÃ­ch thÆ°á»›c khÃ´ng khá»›p: A({m},{n}) @ B({n2},{p})"
        
        C = zeros((m, p))
        for i in range(m):
            for j in range(p):
                for k in range(n):
                    C[i][j] += A[i][k] * B[k][j]
        return C
    
    # TrÆ°á»ng há»£p tensor 4D
    elif dim_A == 4 and dim_B == 4:
        batch, heads, seq_len_q, head_dim_A = len(A), len(A[0]), len(A[0][0]), len(A[0][0][0])
        batch_B, heads_B, seq_len_k_or_dim, dim_or_seq_len_k = len(B), len(B[0]), len(B[0][0]), len(B[0][0][0])
        
        # Kiá»ƒm tra kÃ­ch thÆ°á»›c
        assert batch == batch_B and heads == heads_B, "Batch hoáº·c heads khÃ´ng khá»›p"
        
        # Khá»Ÿi táº¡o káº¿t quáº£
        if transpose_B:
            # B cÃ³ shape [batch, heads, seq_len_k, head_dim]
            seq_len_k = seq_len_k_or_dim
            head_dim_B = dim_or_seq_len_k
            assert head_dim_A == head_dim_B, "KÃ­ch thÆ°á»›c head_dim khÃ´ng khá»›p"
            result = zeros((batch, heads, seq_len_q, seq_len_k))
            
            # Thá»±c hiá»‡n phÃ©p nhÃ¢n ma tráº­n
            for b in range(batch):
                for h in range(heads):
                    for i in range(seq_len_q):
                        for j in range(seq_len_k):
                            sum_val = 0
                            for k in range(head_dim_A):
                                sum_val += A[b][h][i][k] * B[b][h][j][k]  # LÆ°u Ã½ transpose B
                            result[b][h][i][j] = sum_val
        else:
            # B cÃ³ shape [batch, heads, head_dim, seq_len_k]
            head_dim_B = seq_len_k_or_dim
            seq_len_k = dim_or_seq_len_k
            assert head_dim_A == head_dim_B, "KÃ­ch thÆ°á»›c head_dim khÃ´ng khá»›p"
            result = zeros((batch, heads, seq_len_q, seq_len_k))
            
            # Thá»±c hiá»‡n phÃ©p nhÃ¢n ma tráº­n
            for b in range(batch):
                for h in range(heads):
                    for i in range(seq_len_q):
                        for j in range(seq_len_k):
                            sum_val = 0
                            for k in range(head_dim_A):
                                sum_val += A[b][h][i][k] * B[b][h][k][j]
                            result[b][h][i][j] = sum_val
        
        return result
    else:
        raise ValueError(f"KhÃ´ng há»— trá»£ matmul cho tensor chiá»u {dim_A} vÃ  {dim_B}")

def transpose(A, dim1=None, dim2=None):
    """
    Chuyá»ƒn vá»‹ tensor
    - Náº¿u A lÃ  ma tráº­n 2D: káº¿t quáº£ lÃ  A.T thÃ´ng thÆ°á»ng
    - Náº¿u A lÃ  tensor 4D vÃ  dim1=-2, dim2=-1: chuyá»ƒn vá»‹ 2 chiá»u cuá»‘i
    """
    # XÃ¡c Ä‘á»‹nh sá»‘ chiá»u cá»§a A
    if not isinstance(A[0], list):  # A lÃ  vector 1D
        dim_A = 1
        return A  # Vector khÃ´ng Ä‘á»•i khi chuyá»ƒn vá»‹
    elif not isinstance(A[0][0], list):  # A lÃ  ma tráº­n 2D
        dim_A = 2
    elif not isinstance(A[0][0][0], list):  # A lÃ  tensor 3D
        dim_A = 3
    else:  # A lÃ  tensor 4D
        dim_A = 4
    
    # TrÆ°á»ng há»£p ma tráº­n 2D
    if dim_A == 2:
        m, n = len(A), len(A[0])
        result = zeros((n, m))
        for i in range(m):
            for j in range(n):
                result[j][i] = A[i][j]
        return result
    
    # TrÆ°á»ng há»£p tensor 4D - chá»‰ há»— trá»£ chuyá»ƒn vá»‹ 2 chiá»u cuá»‘i
    elif dim_A == 4:
        if (dim1 == -2 and dim2 == -1) or (dim1 == 2 and dim2 == 3):
            batch, heads, seq_len, dim = len(A), len(A[0]), len(A[0][0]), len(A[0][0][0])
            result = zeros((batch, heads, dim, seq_len))
            
            for b in range(batch):
                for h in range(heads):
                    for i in range(seq_len):
                        for j in range(dim):
                            result[b][h][j][i] = A[b][h][i][j]
            return result
        else:
            raise ValueError("Tensor 4D: Chá»‰ há»— trá»£ chuyá»ƒn vá»‹ 2 chiá»u cuá»‘i (dim1=-2, dim2=-1)")
    else:
        raise ValueError(f"KhÃ´ng há»— trá»£ transpose cho tensor chiá»u {dim_A}")

def softmax(x, dim=-1):
    """
    Ãp dá»¥ng softmax
    - x lÃ  ma tráº­n 2D: softmax theo hÃ ng (dim=-1) hoáº·c cá»™t (dim=0)
    - x lÃ  tensor 4D: softmax theo chiá»u cuá»‘i cÃ¹ng (dim=-1)
    """
    # XÃ¡c Ä‘á»‹nh sá»‘ chiá»u cá»§a x
    if not isinstance(x[0], list):  # x lÃ  vector 1D
        dim_x = 1
        # Softmax cho vector 1D
        max_val = max(x)
        exps = [math.exp(v - max_val) for v in x]
        sum_exps = sum(exps)
        return [v / sum_exps for v in exps]
    elif not isinstance(x[0][0], list):  # x lÃ  ma tráº­n 2D
        dim_x = 2
        m, n = len(x), len(x[0])
        result = zeros((m, n))
        
        if dim == -1 or dim == 1:  # Softmax theo hÃ ng
            for i in range(m):
                row = x[i]
                max_val = max(row)
                exps = [math.exp(v - max_val) for v in row]
                sum_exps = sum(exps)
                result[i] = [v / sum_exps for v in exps]
        elif dim == 0:  # Softmax theo cá»™t
            for j in range(n):
                col = [x[i][j] for i in range(m)]
                max_val = max(col)
                exps = [math.exp(v - max_val) for v in col]
                sum_exps = sum(exps)
                for i in range(m):
                    result[i][j] = exps[i] / sum_exps
        
        return result
    elif not isinstance(x[0][0][0], list):  # x lÃ  tensor 3D
        dim_x = 3
        raise ValueError("KhÃ´ng há»— trá»£ softmax cho tensor 3D")
    else:  # x lÃ  tensor 4D
        dim_x = 4
        batch, heads, seq_len, seq_len_k = len(x), len(x[0]), len(x[0][0]), len(x[0][0][0])
        result = zeros((batch, heads, seq_len, seq_len_k))
        
        if dim == -1 or dim == 3:  # Softmax theo chiá»u cuá»‘i
            for b in range(batch):
                for h in range(heads):
                    for i in range(seq_len):
                        # á»”n Ä‘á»‹nh sá»‘ há»c: trá»« max
                        row = x[b][h][i]
                        max_val = max(row)
                        exps = [math.exp(v - max_val) for v in row]
                        sum_exps = sum(exps)
                        result[b][h][i] = [v / sum_exps for v in exps]
        else:
            raise ValueError("Tensor 4D: Chá»‰ há»— trá»£ softmax theo chiá»u cuá»‘i (dim=-1)")
        
        return result

def dropout(x, p=0.0, training=True):
    """
    Ãp dá»¥ng dropout cho tensor
    - Há»— trá»£ tensor 2D vÃ  4D
    """
    if not training or p == 0.0:
        return x
    
    # XÃ¡c Ä‘á»‹nh sá»‘ chiá»u cá»§a x
    if not isinstance(x[0], list):  # x lÃ  vector 1D
        dim_x = 1
        n = len(x)
        result = [0] * n
        for i in range(n):
            if random.random() > p:
                result[i] = x[i] / (1 - p)
        return result
    elif not isinstance(x[0][0], list):  # x lÃ  ma tráº­n 2D
        dim_x = 2
        m, n = len(x), len(x[0])
        result = zeros((m, n))
        for i in range(m):
            for j in range(n):
                if random.random() > p:
                    result[i][j] = x[i][j] / (1 - p)
        return result
    elif not isinstance(x[0][0][0], list):  # x lÃ  tensor 3D
        dim_x = 3
        raise ValueError("KhÃ´ng há»— trá»£ dropout cho tensor 3D")
    else:  # x lÃ  tensor 4D
        dim_x = 4
        batch, heads, seq_len, dim = len(x), len(x[0]), len(x[0][0]), len(x[0][0][0])
        result = zeros((batch, heads, seq_len, dim))
        
        for b in range(batch):
            for h in range(heads):
                for i in range(seq_len):
                    for j in range(dim):
                        if random.random() > p:
                            result[b][h][i][j] = x[b][h][i][j] / (1 - p)
        
        return result

def add(x, y, broadcast_y=False):
    """
    Cá»™ng hai tensor
    - Há»— trá»£ tensor 2D vÃ  4D
    - Náº¿u broadcast_y=True vÃ  x lÃ  tensor 4D: y Ä‘Æ°á»£c coi lÃ  [batch, 1, seq_len_q, seq_len_k]
    """
    # XÃ¡c Ä‘á»‹nh sá»‘ chiá»u cá»§a x
    if not isinstance(x[0], list):  # x lÃ  vector 1D
        dim_x = 1
        n = len(x)
        result = [0] * n
        for i in range(n):
            result[i] = x[i] + y[i]
        return result
    elif not isinstance(x[0][0], list):  # x lÃ  ma tráº­n 2D
        dim_x = 2
        m, n = len(x), len(x[0])
        result = zeros((m, n))
        for i in range(m):
            for j in range(n):
                result[i][j] = x[i][j] + y[i][j]
        return result
    elif not isinstance(x[0][0][0], list):  # x lÃ  tensor 3D
        dim_x = 3
        raise ValueError("KhÃ´ng há»— trá»£ add cho tensor 3D")
    else:  # x lÃ  tensor 4D
        dim_x = 4
        batch, heads, seq_len_q, seq_len_k = len(x), len(x[0]), len(x[0][0]), len(x[0][0][0])
        result = zeros((batch, heads, seq_len_q, seq_len_k))
        
        if broadcast_y:
            # y cÃ³ shape [batch, 1, seq_len_q, seq_len_k]
            for b in range(batch):
                for h in range(heads):
                    for i in range(seq_len_q):
                        for j in range(seq_len_k):
                            result[b][h][i][j] = x[b][h][i][j] + y[b][0][i][j]
        else:
            # y cÃ³ shape [batch, heads, seq_len_q, seq_len_k]
            for b in range(batch):
                for h in range(heads):
                    for i in range(seq_len_q):
                        for j in range(seq_len_k):
                            result[b][h][i][j] = x[b][h][i][j] + y[b][h][i][j]
        
        return result

def scale(x, scaling_factor):
    """
    NhÃ¢n tensor vá»›i má»™t scalar
    - Há»— trá»£ tensor 2D vÃ  4D
    """
    # XÃ¡c Ä‘á»‹nh sá»‘ chiá»u cá»§a x
    if not isinstance(x[0], list):  # x lÃ  vector 1D
        dim_x = 1
        return [v * scaling_factor for v in x]
    elif not isinstance(x[0][0], list):  # x lÃ  ma tráº­n 2D
        dim_x = 2
        m, n = len(x), len(x[0])
        result = zeros((m, n))
        for i in range(m):
            for j in range(n):
                result[i][j] = x[i][j] * scaling_factor
        return result
    elif not isinstance(x[0][0][0], list):  # x lÃ  tensor 3D
        dim_x = 3
        raise ValueError("KhÃ´ng há»— trá»£ scale cho tensor 3D")
    else:  # x lÃ  tensor 4D
        dim_x = 4
        batch, heads, seq_len, dim = len(x), len(x[0]), len(x[0][0]), len(x[0][0][0])
        result = zeros((batch, heads, seq_len, dim))
        
        for b in range(batch):
            for h in range(heads):
                for i in range(seq_len):
                    for j in range(dim):
                        result[b][h][i][j] = x[b][h][i][j] * scaling_factor
        
        return result

def repeat_kv(hidden_states, n_rep):
    """
    Láº·p láº¡i tensor key/value cho GQA (Grouped-Query Attention)
    hidden_states: [batch, num_key_value_heads, seqlen, head_dim]
    n_rep: sá»‘ láº§n láº·p cho má»—i head (num_attention_heads / num_key_value_heads)
    return: [batch, num_attention_heads, seqlen, head_dim]
    """
    if n_rep == 1:
        return hidden_states
    
    batch, num_kv_heads, seq_len, head_dim = len(hidden_states), len(hidden_states[0]), len(hidden_states[0][0]), len(hidden_states[0][0][0])
    output = zeros((batch, num_kv_heads * n_rep, seq_len, head_dim))

    for b in range(batch):
        for h in range(num_kv_heads):
            for r in range(n_rep):
                new_h = h * n_rep + r
                for i in range(seq_len):
                    for d in range(head_dim):
                        output[b][new_h][i][d] = hidden_states[b][h][i][d]

    return output

